import React, { useRef, useEffect, useState } from 'react';
import Contact from "./Components/Contact"
import Project from './Components/Project';
import Home from './Components/Home';
import Update from './Components/Update';

import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import "./Components/style.css"

export default function App() {
  function Home() {
    const [scroll, setScroll] = useState(0);

    useEffect(() => {
      function handleScroll() {
        setScroll(window.pageYOffset);
      }

      window.addEventListener('scroll', handleScroll);

      return () => {
        window.removeEventListener('scroll', handleScroll);
      };
    }, []);

    let bodyStyle = {};
    if (scroll < 500) {
      bodyStyle = { background: 'linear-gradient(to top left, green, yellow)' };
    } else if (scroll >= 500 && scroll < 1200) {
      bodyStyle = { background: 'linear-gradient(to top left, yellow, blue)' };
    } else if (scroll >= 1200 && scroll < 1800) {
      bodyStyle = { background: 'linear-gradient(to top left, blue, orange)' };
    } else if (scroll >= 1800 && scroll < 3000) {
      bodyStyle = { background: 'linear-gradient(to top left, orange, red)' };
    } else {
      bodyStyle = { background: 'linear-gradient(to top left, red, purple)' };
    }
  }

  return (
    <div className='body' style={bodyStyle}>
      {/* <div className="container">
       style={{ background: "linear-gradient(to top left, #DBDBF3, #F6D3D7, #BFE1F4)" }}
        <div className="header">
          <NavBar/>
        </div>
        <div className="media">
          <Media />
        </div>
        <div className="content">
          <Tittle />
        </div>
        <div class="footer">
          <About />
        </div>
      </div> */}
      <div className='container'>
        <Home />
        <Project/>
        <Update/>
        <Contact />
      </div>

    </div>

  );
}
