import React, { useState } from 'react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  function handleChange(event) {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  }

  function handleSubmit(event) {
    event.preventDefault();

    // Send formData to your server or use a service like FormSubmit
    console.log(formData);

    // Clear the form fields after submission
    setFormData({
      name: '',
      email: '',
      subject: '',
      message: '',
    });
  }

  return (
    <section className="contact container section" id="contact">
      <h2 className="section_title">âœ¨Get In Touchâœ¨</h2>

      <div className="contact_container grid">
        <div className="contact_info">
          <h3 className="contact_title">Let's get to know each other</h3>
          <p className="contact_details">Shoot me an email! ðŸ˜¬</p>
        </div>

        <form className="contact_form" onSubmit={handleSubmit}>
          <div className="contact_form_group">
            <div className="contact_form_div">
              <input
                type="text"
                className="contact_form_input"
                placeholder="Your name"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
            </div>

            <div className="contact_form_div">
              <input
                type="email"
                className="contact_form_input"
                placeholder="Your email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
          </div>
          <div className="contact_form_div">
            <input
              type="text"
              className="contact_form_input"
              placeholder="Your subject"
              name="subject"
              value={formData.subject}
              onChange={handleChange}
            />
          </div>

          <div className="contact_form_div contact_form_area">
            <textarea
              name="message"
              cols="30"
              rows="10"
              className="contact_form_input"
              placeholder="Your message"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
          </div>
          <button type="submit" name="submit" className="btn">
            SEND!
          </button>
        </form>
      </div>
    </section>
  );
};

export default Contact;
