import React, { Component, useEffect, useState } from 'react';
import Contact from "./Components/Contact"
import Project from './Components/Project';
import Home from './Components/Home';
import Update from './Components/Update';

import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import "./Components/style.css"

export default function App() {

  var body = document.getElementsByTagName('body')[0];
  window.onscroll = function (event) {
    var scroll = window.pageYOffset;
    if (scroll < 500) {
        // green
        body.style.backgroundColor = `linear-gradient(to top left, #DBDBF3, #F6D3D7, #BFE1F4)`;
    } else if (scroll >= 300 && scroll < 600) {
        // yellow
        body.style.backgroundColor = 'yellow';
    } else if (scroll >= 600 && scroll < 1200) {
        // blue
        body.style.backgroundColor = 'blue';
    } else if (scroll >= 1200 && scroll < 1800) {
        // orange
        body.style.backgroundColor = 'orange';
    } else if (scroll >= 1800 && scroll < 3000) {
        // red
        body.style.backgroundColor = 'red';
    } else {
        // purple
        body.style.backgroundColor = 'purple';
    }
}

  return (
    <div className='body' style={{ background: "linear-gradient(to top left, #DBDBF3, #F6D3D7, #BFE1F4)" }}>
      {/* <div className="container">
