import React, { Component, useEffect, useState } from 'react';
import Contact from "./Components/Contact"
import Project from './Components/Project';
import Home from './Components/Home';
import Update from './Components/Update';

import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import "./Components/style.css"

export default function App() {

  const appRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scroll = window.pageYOffset;
      let background = 'linear-gradient(to top left, #DBDBF3, #F6D3D7, #BFE1F4)';

      if (scroll < 500) {
        // green
        background = 'linear-gradient(to top left, #00FF00, #FFFFFF, #00FF00)';
      } else if (scroll >= 500 && scroll < 1000) {
        // yellow
        background = 'linear-gradient(to top left, #FFFF00, #FFFFFF, #FFFF00)';
      } else if (scroll >= 1000 && scroll < 1500) {
        // blue
        background = 'linear-gradient(to top left, #0000FF, #FFFFFF, #0000FF)';
      } else if (scroll >= 1500 && scroll < 2000) {
        // orange
        background = 'linear-gradient(to top left, #FFA500, #FFFFFF, #FFA500)';
      } else if (scroll >= 2000 && scroll < 2500) {
        // red
        background = 'linear-gradient(to top left, #FF0000, #FFFFFF, #FF0000)';
      } else {
        // purple
        background = 'linear-gradient(to top left, #800080, #FFFFFF, #800080)';
      }

      appRef.current.style.background = background;
    };
});

  return (
    <div className='body'>
      {/* <div className="container">
       style={{ background: "linear-gradient(to top left, #DBDBF3, #F6D3D7, #BFE1F4)" }}
